mapper.select:
select f.*,o.org_name as orgName from function_manage f LEFT JOIN org_info o on o.id=f.ORG_ID where 1=1 and f.IS_DELETE = 0 and f.FUNCTION_ID in (select fac.FUNCTION_ID from function_manage fac LEFT JOIN acct_oper_priv_rela aoprc on aoprc.FUNCTION_ID=fac.FUNCTION_ID LEFT JOIN acct_role_real arrc on arrc.ROLE_ID=aoprc.ROLE_ID where arrc.ACCT_ID=#{acctId} and aoprc.FUNCTION_ID is not null )
AND f.PARENT_APPLICATION_ID = #{parentApplicationId}
AND f.STATUS = #{status}

SELECT
CASE NVL( ( SELECT NVL(f.FUNCTION_ID, 0) FROM function_manage f LEFT JOIN acct_oper_priv_rela ao ON ao.FUNCTION_ID = f.function_id LEFT JOIN role_info ri ON ri.ROLE_ID = ao.ROLE_ID WHERE f. STATUS = 1
AND ri.role_id = #{roleId}
AND f1.function_id = f.function_id ), 0 ) WHEN 0 THEN 0 ELSE 1 END AS STATUS，f1.* FROM function_manage f1 WHERE f1. STATUS = 1 AND f1. STATUS = 1 AND f1.IS_DELETE = 0
AND f1.PARENT_APPLICATION_ID = #{parentApplicationId}
and f1.FUNCTION_ID in (select fac.FUNCTION_ID from function_manage fac LEFT JOIN acct_oper_priv_rela aoprc on aoprc.FUNCTION_ID=fac.FUNCTION_ID LEFT JOIN acct_role_real arrc on arrc.ROLE_ID=aoprc.ROLE_ID where arrc.ACCT_ID=#{acctId} and aoprc.FUNCTION_ID is not null )

select * from function_manage f
where 1=1 and f.IS_DELETE=0

  AND f.FUNCTION_TITLE = #{functionTitle}

select * from function_manage f
where 1=1 and f.IS_DELETE=0

  AND f.FUNCTION_TITLE = #{functionTitle}

SELECT D.FUNCTION_ID, D.FUNCTION_CODE, D.FUNCTION_DESC, D.ID_FULL_PATH, D.TITLE_FULL_PATH, D.IS_LAST_LEVEL, D.PARENT_APPLICATION_ID, D.STATUS, D.CREATED_BY, D.CREATED_TIME, D.UPDATED_BY, D.UPDATED_TIME,D.FUNCTION_PATH FROM ACCT_INFO A LEFT JOIN ACCT_ROLE_REAL b ON A .ACCT_ID = b.ACCT_ID LEFT JOIN ACCT_OPER_PRIV_RELA C ON B.ROLE_ID = C.ROLE_ID LEFT JOIN FUNCTION_MANAGE D ON C.FUNCTION_ID = D.FUNCTION_ID where 1=1 and d.IS_DELETE=0
AND d.FUNCTION_TITLE = #{functionTitle}
AND a.ACCT_ID = #{acctId}
