mapper.select:
SELECT MSO.STATE, MSO.CREATED_DT, MSO.DATA_STATE, MSO.MILL_SHEET_NO_OLD, MSO.MILL_SHEET_NO, MSO.SPECIAL_NEED, MSO.PRINTABLE_NUM, MSO.PRINTED_NUM, MSO.MILL_SHEET_URL, MSO.MILL_SHEET_NAME, MSO.DEPT_CODE, MSO.DOWNABLE_NUM, MSO.DOWN_NUM, MSO.HAVE_STATE, MSO.SPLIT_STATE, MSO.MILL_SHEET_TYPE, MSO.SURPLUS_BATCH, MSO.ZKUNWE, MSH.ZCHEHAO, MSH.ZHTH, MSH.ZCPMC, MSH.ZPH, MSH.ZKDAUF as ZKDAUF, MSO.SID, (select distinct name1 from MILL_CUSTOMER where KUNNR = MSO.ZKUNNR) ZKUNNR FROM WARRANTY.MILL_SHEET_HOSTS MSO LEFT JOIN WARRANTY.MILL_SHEET_HEAD MSH ON MSO.MILL_SHEET_NO = MSH.MILL_SHEET_NO
AND TO_CHAR (MSO.CREATED_DT, 'yyyy-MM-dd hh24:mi:ss') <= #{endDtStr}
AND TO_CHAR (MSO.CREATED_DT, 'yyyy-MM-dd hh24:mi:ss') >= #{startDtStr}
AND MSO.MILL_SHEET_NO = #{millSheetNo,jdbcType=VARCHAR}
AND MSO.MILL_SHEET_NO in
#{millSheetNos[${index}]}
AND MSH.ZCHEHAO = #{zchehao,jdbcType=VARCHAR}
AND MSH.ZHTH = #{zhth,jdbcType=VARCHAR}
AND MSH.SPILT_CUSTOMER = #{zkunnr,jdbcType=VARCHAR}
AND MSH.ZPH = #{zph,jdbcType=VARCHAR}
AND MSH.SPILT_CUSTOMER = #{spiltCustomer,jdbcType=VARCHAR}
AND MSO.STATE IN ('EXAMINED', 'DOWNLOADED', 'PRIVIEWED', 'PRINTED')
